import { useState } from "react";

const Example = () => {
  const [checkbox, setCheckbox] = useState(true);
  return (
    <>
      <label htmlFor="">
        チェック：
        <input
          type="checkbox"
          checked={checkbox}
          onChange={ () => setCheckbox(prev => !prev)} />
      </label>
      <h3>{ checkbox ? "ON" : "OFF" }</h3>
    </>
  );
};

export default Example;
